function openModal(){document.getElementById("myModal").style.display="block"}function closeModal(){document.getElementById("myModal").style.display="none"}function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){var l,n=document.getElementsByClassName("mySlides"),d=document.getElementsByClassName("demo");document.getElementById("caption");for(e>n.length&&(slideIndex=1),e<1&&(slideIndex=n.length),l=0;l<n.length;l++)n[l].style.display="none";for(l=0;l<d.length;l++)d[l].className=d[l].className.replace(" active","");n[slideIndex-1].style.display="block"}AOS.init({duration:1200});var slideIndex=1;showSlides(slideIndex);